const eventListsAPI = (() => {
  const EVENT_API = "http://localhost:3000/events";

  // GET
  const getAll = async () => {
    const response = await fetch(EVENT_API);
    const events = await response.json();
    return events;
  };

  // POST
  const add = async (newEvent) => {
    const res = await fetch(EVENT_API, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newEvent),
    });
    const event = await res.json();
    // we need the id of the new event
    // because id should be generated by the server
    return event;
  };

  const edit = async (id, updatedEvent) => {
      const res = await fetch(`${EVENT_API}/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedEvent),
    });
    const event = await res.json();
    return event;
  };

  // DELETE
  const deleteById = async (id) => {
    await fetch(`${EVENT_API}/${id}`, {
      method: "DELETE",
    });
  };

  return {
    getAll,
    add,
    edit,
    deleteById,
  };
})();